scene.onOverlapTile(SpriteKind.Player, assets.tile`myTile3`, function (sprite, location) {
    james.vy = -150
})
scene.onOverlapTile(SpriteKind.Player, sprites.dungeon.hazardLava1, function (sprite, location) {
    music.play(music.melodyPlayable(music.powerDown), music.PlaybackMode.UntilDone)
    game.splash("Game over press A to contunie ")
    if (controller.A.isPressed()) {
        tiles.placeOnTile(james, tiles.getTileLocation(0, 7))
        for (let value4 of tiles.getTilesByType(assets.tile`myTile0`)) {
            tiles.setTileAt(value4, assets.tile`transparency16`)
            level = level
        }
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.coin, function (sprite, otherSprite) {
    info.changeScoreBy(1)
    sprites.destroy(otherSprite)
    music.play(music.melodyPlayable(music.baDing), music.PlaybackMode.UntilDone)
})
info.onLifeZero(function () {
    music.play(music.melodyPlayable(music.powerDown), music.PlaybackMode.UntilDone)
    game.splash("Game over press A to contunie ")
    if (controller.A.isPressed()) {
        tiles.placeOnTile(james, tiles.getTileLocation(0, 7))
        for (let value5 of tiles.getTilesByType(assets.tile`myTile0`)) {
            tiles.setTileAt(value5, assets.tile`transparency16`)
            level = level
        }
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.fireballs, function (sprite, otherSprite) {
    info.changeLifeBy(-1)
    sprites.destroy(otherSprite, effects.warmRadial, 1000)
    music.play(music.melodyPlayable(music.powerDown), music.PlaybackMode.UntilDone)
})
